func TestBubbleSort(t *testing.T) {
	for idx := range sortCases {
		BubbleSort(sortCases[idx])
		if !reflect.DeepEqual(sortCases[idx], sortAnswers[idx]) {
			t.Errorf("wrong answer on test %d\n got: %v\n expected: %v", idx, sortCases[idx], sortAnswers[idx])
		}
	}
}

func BenchBubbleSort(b *testing.B) {
	sortData := make([]int, len(sortBenchData))
	copy(sortData, sortBenchData)

	BubbleSort(sortData)
	if !reflect.DeepEqual(sortData, sortBenchAnswer) {
		b.Errorf("wrong answer on benchmark\n got: %v\n expected: %v", sortBenchData, sortBenchAnswer)
	}
}
